              };

              if (navigator.userAgent.match(/Android/i)) {
                dev = 'android';
              }
              else if (navigator.userAgent.match(/iPhone/i) || navigator.userAgent.match(/iPod/i) || navigator.userAgent.match(/iPad/i)) {
                dev = "ios";
              }

              if (dev) {
                var page = window.location.pathname;
                if (page == 'index.html') {
                  page = 'homepage';
                }
                var path = data[dev].app  + page;
                $('a[href="https://wwws.mint.com/login.event?task=S"], a[href="https://wwws.mint.com/signupJumper.event"]').each(function() {
                  $(this).attr('href',path);
                });
              }

              if (excludedPages.indexOf(window.location.pathname.split('index.html')[1]) >= 0) {
                return;
              }

              var $cta = $("[data-wa-link='mobile hero get app']");

              if (dev) {
                if ($('.mobile_inter').length > 0) {
                  showInterstitial();
                }
                else {
                  setTimeout(showInterstitial,1000);
                }
              }

              function showInterstitial() {
                $('#sidr-wrapper-0 span').css('background','#000');
                $cta.attr('href', data[dev].app + 'homepage-cta');
              }

          }(jQuery));
